class Solution {
public:
    int dp[105];
    vector<int> num;
    int solve(int id, int n) {
        if(id >= n) return 0;
        if(dp[id] != -1) return dp[id];
        int ans = num[id];
        ans = max(ans, num[id] + solve(id + 2, n));
        ans = max(ans, num[id] + solve(id + 3, n));
        return dp[id] = ans;
    }
    int rob(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        int n = nums.size();
        num = nums;
        int ans;
        if(n == 1) ans = solve(0, n);
        else {
            ans = solve(0, n-1);
            memset(dp, -1, sizeof(dp));
            ans = max(ans, solve(1, n));
            memset(dp, -1, sizeof(dp));
            ans = max(ans, solve(2, n));
        }
        return ans;
    }
};
