class Solution(object):
    def isPerfectSquare(self, num):
        """
        :type num: int
        :rtype: bool
        """
        n=num
        if n == 0: 
            return True
        elif n<0:
            return False
        elif n==1:
            return True
        x=1
        while x*x<n:
            x=x*2
        if x*x==n:
            return True
        no=0
        while no==0:
            x=floor(x/1.2)

            if x*x<=n:
                no=1
        b=ceil(x/30)
        while x*x<n:
            x=x+b
        if x*x==n:
            return True
        while x*x>n:
            x=x-1
        if x*x==n:
            return True
        else:
            return False
