class Solution {
    public int reachNumber(int target) {
        target = Math.abs(target);
        int step = 0;
        int sum = 0;
        while (sum < target) {
            step++;
            sum += step;
        }
        while ((sum - target) % 2 != 0) {
            step++;
            sum += step;
        }
        return step;
    }
}

int reachNumber(int target) {
        int t = abs(target), step = 0, sum = 0;
        while(sum < t) sum += ++step;
        while(abs(sum-t) % 2) sum += ++step;
        return step;
}

