int minSwapsCouples(vector<int>& row) {
       int map[62],n=row.size(),count=0;
       for(int i=0;i<61;i++)
           map[i]=-1;
       for(int i=0;i<n;i++)
           map[row[i]]=i;
       for(int i=0;i<n;i+=2)
       {
           if(row[i]%2==0)
           {
               if(row[i+1]==row[i]+1)
                   continue;
               else
               {
                   int index=map[row[i]+1];
                   map[row[i+1]]=index;
                   map[row[i]+1]=i+1;
                   swap(row[i+1],row[index]);
                   count++;
               }
           }
           else
           {
               if(row[i+1]==row[i]-1)
                   continue;
               else
               {
                   int index=map[row[i]-1];
                   map[row[i+1]]=index;
                   map[row[i]-1]=i+1;
                   swap(row[i+1],row[index]);
                   count++;
               }
           }
       }
       return count;
   }
