public int[] numberOfLines(int[] widths, String s) {
    int sum =0;
    int count =1;
    for(int i=0; i<s.length(); i++){
       char ch = s.charAt(i);
       int p = ch - 'a';
       if(widths[p] + sum >100){
           count++;
           sum = widths[p];
       }
        else{
            sum = sum + widths[p];
        }
   } 
    int [] arr = {count, sum};
    return arr;
}
