def numComponents(self, head, G):
    setG = set(G)
    res = 0
    while head:
        if head.val in setG and (head.next == None or head.next.val not in setG):
            res += 1
        head = head.next
    return res

class Solution {
    public int numComponents(ListNode head, int[] nums) {
        int max=0;
        for(int x:nums)
        {
            if(x>max)
                max=x;
        }
        int[] a= new int[max+1];

        for(int x:nums)
            a[x]++;

        int c=0;
        int components=0;
        while(head!=null)
        {
            if(head.val<=max&&a[head.val]==1){
                c++;
            }
            else{
                if(c>0){
                    components++;
                    c=0;
                }
            }
            head=head.next;
        }
        if(c>0)
            components++;
        return components;
    }
}
