class Solution(object):
    def strWithout3a3b(self, A, B):
        if A == 0:      
   return 'b' * B
        elif B == 0:    
   return 'a' * A
        elif A == B:    
   return 'ab' * A
        elif A > B:     
   return 'aab' + self.strWithout3a3b(A - 2, B - 1)
        else:           
   return self.strWithout3a3b(A - 1, B - 2) + 'abb'

class Solution {
public:
    string strWithout3a3b(int a, int b) {
        string ans="";

        // while both 'a' and 'b' are available
        while(a && b){
            if(a>b){        // case 1
                ans+= "aab";
                a-=2;
                b--;
            }
            else if(b>a){   //case 2
                ans+= "bba";
                b-=2;
                a--;
            }
            else{          //case 3
                ans+= "ab";
                a--;
                b--;
            }
        }

        // when only 'a' is available
        while(a){
            ans+="a";
            a--;
        }

        // when only 'b' is available
        while(b){
            ans+= "b";
            b--;
        }

        return ans;
    }
};
