class Solution(object):
    def printVertically(self, s):
        """
        :type s: str
        :rtype: List[str]
        """
        s=s.split()
        ln=max([len(i) for i in s])
        lst=[]
        for i in s:
            if i!=ln:
                lst.append(i+" "*(ln-len(i)))
            else:
                lst.append(i)
        final=[]
        for i in zip(*lst):
            a="".join(i)
            final.append(a.rstrip(' '))
        return final

