def largestMultipleOfThree(self, digits):
    dp = [-1,-1,-1]
    for a in sorted(digits)[::-1]:
        for x in dp[:] + [0]:
            y = x * 10 + a
            dp[y % 3] = max(dp[y % 3], y)
    return str(dp[0]) if dp[0] >= 0 else ""

class Solution {
public:


    string largestMultipleOfThree(vector<int>& digits) {     
        sort(digits.begin(),digits.end());
        string s="";
        int n=digits.size();
         int sum=0;
        int i;
        int check=0;
        int flag=0;
        for(i=0;i<n;i++){
            s=s+to_string(digits[i]);
            sum=sum+digits[i];
        }
         reverse(s.begin(),s.end());
       if(sum%3==0){

           if(s[0]=='0'){
               return "0";
           }
           return s;
       }
      else if(sum%3==1){
           i=s.size()-1;
          while(i>=0){
               int c=s[i]-'0';
               if(c%3==1){
                   s.erase(i,1);
                   flag=1;
                   break;
               }
               i--;
          }
          if(flag==0){
              i=s.size()-1;
              while(i>=0){
             int c=s[i]-'0';
             if(c%3==2){
                 s.erase(i,1);
                  check++;
                  if(check==2){
                      break;
                  }
             }
             i--;
              }

          }
          sort(s.begin(),s.end());
          reverse(s.begin(),s.end());
          if(s[0]=='0'){
              return "0";
          }
          return s;

       }
       else if(sum%3==2){
           i=s.size()-1;
          while(i>=0){
               int c=s[i]-'0';
               if(c%3==2){
                   s.erase(i,1);
                   flag=1;
                   break;
               }
               i--;
          }
          if(flag==0){
              i=s.size()-1;
              while(i>=0){
             int c=s[i]-'0';
             if(c%3==1){
                 s.erase(i,1);
                  check++;
                  if(check==2){
                      break;
                  }
             }
             i--;
              }

          }
          sort(s.begin(),s.end());
          reverse(s.begin(),s.end());
          if(s[0]=='0'){
              return "0";
          }
          return s;


       }
     return s;

    }
};
