public int sumFourDivisors(int[] nums) {
    int sum = 0;
    for (int n : nums) {
        int last_d = 0;
        for (int d = 2; d * d <= n; ++d) {
            if (n % d == 0) {
                if (last_d == 0)
                    last_d = d;
                else {
                    last_d = 0;
                    break;
                }
            }
        }
        if (last_d > 0 && last_d != n / last_d) {
            sum += 1 + n + last_d + n / last_d;
        }            
    }
    return sum;        
}

class Solution {
public:
    int sumFourDivisors(vector<int>& nums) {
        int ans = 0, sum, cnt;
        for(auto &num: nums) {
            sum = 0;
            cnt = 0;
            for(int j=1; j*j<=num; j++) {
                if (num%j==0) {
                    if (num/j == j) {
                        sum += j;
                        cnt++;
                    } else {
                        sum += j + (num/j);
                        cnt += 2;
                    }
                }
            }
            if (cnt == 4) ans += sum;
        }
        return ans;
    }
};

class Solution {
    public int sumFourDivisors(int[] nums) {
        int sum = 0;
        for(int item : nums) {
            int count = 0;
            int curSum = 0;
            for(int i = 1; i * i <= item; i++) {

                if(item % i == 0) {
                    count += 1;
                    curSum += i;
                    if(item / i != i){
                        count ++;
                        curSum += item / i; 
                    }
                }
            }
            if(count == 4) sum += curSum;
        }
        return sum;
    }
}

