public static int missingNumber(int[] nums) {
    int sum = nums.length;
    for (int i = 0; i < nums.length; i++)
        sum += i - nums[i];

public class Solution {
    public int missingNumber(int[] nums) {
     int sum=0;
      int len=nums.length+1;
       for(int n:nums)
       sum+=n;
      return ((len*(len-1)) /2-sum);

class Solution {
public:
    int missingNumber(vector<int>& nums) 
    {
        int n = nums.size() ;
        int sum = n * (n + 1)/2 ;
        for (int i = 0 ; i < n ; i ++)
        {
            sum = sum - nums[i] ;
        }
        return sum ;
    }
};
