def repeatedNTimes(self, A):
    """
    :type A: List[int]
    :rtype: int
    """
    return int((sum(A)-sum(set(A))) // (len(A)//2-1))

class Solution:
    def repeatedNTimes(self, A):
        for i in range(len(A)):
            if A[i - 1] == A[i] or A[i - 2] == A[i]:
                return A[i]
