class Solution {
    public int[] sumEvenAfterQueries(int[] nums, int[][] queries) 
    {
        int arr[] = new int[nums.length];
        int sum = sumOfArray(nums);
        int i=0;
        for(int query[]:queries)
        {
            if(nums[query[1]]%2==0)
                sum-=nums[query[1]];
            nums[query[1]]+=query[0];
            if(nums[query[1]]%2==0)
                sum+=(nums[query[1]]);
            arr[i++]=sum;
        }
        return arr;
    }
    public int sumOfArray(int arr[])
    {
        int sum=0;
        for(int a:arr)
        {
            if(a%2==0)
                sum+=a;
        }
        return sum;
    }
}

class Solution {
    public int[] sumEvenAfterQueries(int[] nums, int[][] queries) {
        int sum = 0;
        for(int i : nums){
            if(i % 2 == 0){
                sum += i;
            }
        }
        int m = queries.length;
        int[] ans = new int[m];
        for(int i = 0; i < m; i++){
            if(queries[i][0] % 2 == 0){
                if(nums[queries[i][1]] % 2 == 0){
                    sum += queries[i][0];
                }
            }else{
                if(nums[queries[i][1]] % 2 != 0){
                    sum += nums[queries[i][1]] + queries[i][0];
                }else{
                    sum -= nums[queries[i][1]];
                }
            }
            nums[queries[i][1]] += queries[i][0];
            ans[i] = sum;
        }
        return ans;
    }
}
