class Solution {
public:
    int longestDecomposition(string s) {
        int n=s.size(),i=0,j=n-1,ans=0;
        string a="";
        while(i<j){
            a+=s[i];
            if(s[i]==s[j]){
                int len=a.size();
                if(a==s.substr(j-len+1,len)){
                    ans+=2;j=j-len;
                    a="";
                }
            }i++;            
        }
        if(i==j) ans++;
        return ans;
    }
};
