class Solution:
    def minimumAbsDifference(self, A):
        ans, min_diff = [], 10**10
        for i in range(len(A)):
            for j in range(i+1, len(A)):
                a, b = sorted((A[i], A[j]))
                cur_diff = b - a
                if cur_diff == min_diff:
                    ans.append([a, b])
                elif cur_diff < min_diff:
                    min_diff, ans = cur_diff, [[a, b]]
        return sorted(ans)

class Solution:
    def minimumAbsDifference(self, A):
        A.sort()
        ans, min_diff = [], 10**10
        for i in range(len(A)):
            a, b = sorted((A[i], A[i-1]))
            cur_diff = b - a
            if cur_diff == min_diff:
                ans.append((a, b))
            elif cur_diff < min_diff:
                min_diff, ans = cur_diff, [(a, b)]
        return ans

class Solution:
    def minimumAbsDifference(self, A):
        mn, mx = min(A), max(A)
        R, shift, min_diff, prev_element = mx-mn+1, -mn, 10**10, -10**10
        ans, seen = [], [False]*R
        for c in A:
            seen[c + shift] = True
        for i in range(R):
            if seen[i]:
                a, b  = prev_element - shift, i - shift
                cur_diff = b-a
                if cur_diff == min_diff: ans.append([a, b])
                elif cur_diff < min_diff:
                    min_diff, ans = cur_diff, [[a, b]]
                prev_element = i
        return ans

class Solution {
    public List<List<Integer>> minimumAbsDifference(int[] arr) {
        Arrays.sort(arr);
        int min=Integer.MAX_VALUE;
        for(int i=0;i<arr.length-1;i++){
            int d=arr[i+1]-arr[i];
            min=Math.min(min,d);
        }

        List<List<Integer>> list=new ArrayList<>();
    for(int i=0;i<arr.length-1;i++){
        if(arr[i+1]-arr[i]==min)  list.add(Arrays.asList(arr[i],arr[i+1]));

    }
        return list;
    }
