class Solution(object):
    def arrangeWords(self, text):
        """
        :type text: str
        :rtype: str
        """
        text=list(text.split())
        lst=[]
        c=0
        for i in sorted(text,key=len):
            if c==0:
                lst.append(i.title())
                c+=1
            else:
                lst.append(i.lower())
        return " ".join(lst)

class Solution {
public:
    string arrangeWords(string text) {
        transform(text.begin(), text.begin()+1,text.begin(), ::tolower);
        map<int,vector<string> > m;
        string str = "";
        int l = text.length();
        for(int i=0; i<l; ++i){
            if(text[i] != ' '){
                str += text[i];
            }
            else{
                int p = str.length();
                m[p].push_back(str);
                str = "";
            }
        }
        int p = str.length();
        m[p].push_back(str);
        string s = "";
        for(auto& itr : m){
            for(auto& i : itr.second){
                s += i + " ";
            }
        }
        s.pop_back();
        transform(s.begin(),s.begin()+1,s.begin(), ::toupper);
        return s;
    }
};
