class Solution {
    public double average(int[] salary) {
         if (salary.length == 0) {
            return 0.00;
        }

        int min = salary[0];
        int max = salary[salary.length - 1];

        int totalSum = 0;

        for (int i = 0; i < salary.length; i++) {
            int curSalary = salary[i];
            totalSum += curSalary;
            if (curSalary < min) {
                min = curSalary;
            }
            if (curSalary > max) {
                max = curSalary;
            }
        }
        totalSum = totalSum - max - min;
        return (double) totalSum / (salary.length - 2);

    }
}

