class Solution:
    def numWaterBottles(self, numBottles: int, numExchange: int) -> int:
        result = numBottles
        drunk, empty = divmod(numBottles, numExchange)
        while drunk:
            result += drunk
            drunk, empty = divmod(empty + drunk, numExchange)
        return result

class Solution {
    public int numWaterBottles(int numBottles, int numExchange) {
      int sum=numBottles;
        while(numBottles>=numExchange){
          int  t=numBottles/numExchange;
            sum=sum+t;
            numBottles=t+(numBottles%numExchange);
        }
        return sum;
    }
}

class Solution {
public:
    int numWaterBottles(int numBottles, int numExchange) {
        int sum =0;
        int left = 0;
        while(numBottles>0)
        {
            sum += numBottles;
            left += numBottles;
            numBottles = (left/numExchange);
            left = left%numExchange;
        }

        return sum;
    }
};
