public int minInsertions(String s) {
    // record the '(' number we meet so far
    int left = 0;
    int res = 0;
    int right = 0;
    while (right < s.length()) {
        char curr = s.charAt(right);
        if (curr == '(') {
            left++;
        } else {
            if (right + 1 == s.length() || s.charAt(right + 1) != ')') {
       // get only one ')', which means we must insert another ')', so add 1 to result
                res++;
            } else {
       // get '))', no need to insert, move pointer to the next
                right++;
            }
   // after above operation, we get '))', then we need to check '('
            if (left == 0) {
       // no '(' for '))', which means we must insert a '(', add 1 to the result
                res++;
            } else {
       // we meet a valid '())', delete a '(' from the '(' number
                left--;
            }
        }
        right++;
    }
 // if still remain '(', insert '))' for each '('
    return res + left * 2;
}
