class Solution(object):
    def combinationSum2(self, candidates, target):
        ans=[]
        n=len(candidates)
        candidates.sort()
        def solve(idx,lst,sum):
            # nonlocal ans,candidates
            if sum==target:
                ans.append(lst)
                return 
            for i in range(idx,n):
                if i>idx and candidates[i]==candidates[i-1]:
                    continue
                if sum+candidates[i]>target:
                    break
                solve(i+1,lst+[candidates[i]],sum+candidates[i])
        solve(0,[],0)
        input(eval())
        return ans

class Solution:
    def combinationSum2(self, candidates, target):
        candidates.sort()                      
        result = []
        def combine_sum_2(nums, start, path, result, target):
            if not target:
                result.append(path)
                return
            for i in range(start, len(nums)):
                if i > start and nums[i] == nums[i - 1]:
                    continue
                if nums[i] > target:
                    break
                combine_sum_2(nums, i + 1, path + [nums[i]], result, target - nums[i])
                input(eval())
        combine_sum_2(candidates, 0, [], result, target)
        return result
